import React, { useState, useEffect } from 'react';
import axios from 'axios';

const ItemForm = ({ fetchItems, selectedItem, clearSelection }) => {
  const [form, setForm] = useState({
    name: selectedItem?.name || '',
    description: selectedItem?.description || '',
    price: selectedItem?.price || '',
  });

  useEffect(() => {
    if (selectedItem) {
      setForm({
        name: selectedItem.name,
        description: selectedItem.description,
        price: selectedItem.price,
      });
    }
  }, [selectedItem]);  // Mettre à jour les champs lorsque selectedItem change

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      if (selectedItem) {
        // Mise à jour de l'élément
        await axios.put(`http://localhost:5000/items/${selectedItem._id}`, form);
      } else {
        // Création de l'élément
        await axios.post('http://localhost:5000/items', form);
      }
      fetchItems();   // Mettre à jour la liste après ajout ou modification
      clearSelection(); // Annuler la sélection de l'élément
      setForm({ name: '', description: '', price: '' }); // Réinitialiser le formulaire
    } catch (error) {
      console.error('Error submitting form:', error);
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <input
        type="text"
        name="name"
        value={form.name}
        onChange={handleChange}
        placeholder="Name"
        required
      />
      <textarea
        name="description"
        value={form.description}
        onChange={handleChange}
        placeholder="Description"
      ></textarea>
      <input
        type="number"
        name="price"
        value={form.price}
        onChange={handleChange}
        placeholder="Price"
        required
      />
      <button type="submit">{selectedItem ? 'Update' : 'Add'} Item</button>
      {selectedItem && <button onClick={clearSelection}>Cancel</button>}
    </form>
  );
};

export default ItemForm;
