import React from 'react';
import axios from 'axios';

const ItemList = ({ items, fetchItems, selectItem }) => {
  const handleDelete = async (id) => {
    try {
      await axios.delete(`http://localhost:5000/items/${id}`);
      fetchItems(); // Rafraîchir la liste après la suppression
    } catch (error) {
      console.error(error);
    }
  };

  // Vérifier si 'items' est un tableau avant d'utiliser '.map()'
  if (!Array.isArray(items)) {
    return <div>Loading...</div>; // Afficher un message de chargement si 'items' n'est pas un tableau
  }

  return (
    <ul>
      {items.map((item) => (
        <li key={item._id}>
          <strong>{item.name}</strong> - ${item.price}
          <p>{item.description}</p>
          <button onClick={() => selectItem(item)}>Edit</button>
          <button onClick={() => handleDelete(item._id)}>Delete</button>
        </li>
      ))}
    </ul>
  );
};

export default ItemList;
