import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './App.css'; 

const AdminPanel = () => {
  const [items, setItems] = useState([]);
  const [form, setForm] = useState({
    name: '',
    price: '',
    image: null,
  });
  const [editItemId, setEditItemId] = useState(null);

  // Fetch items from API
  const fetchItems = async () => {
    try {
      const response = await axios.get('http://localhost:5000/items');
      setItems(response.data);
    } catch (error) {
      console.error('Error fetching items:', error);
    }
  };

  useEffect(() => {
    fetchItems();
  }, []);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setForm({ ...form, [name]: value });
  };

  const handleImageChange = (e) => {
    setForm({ ...form, image: e.target.files[0] });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    const formData = new FormData();
    formData.append('name', form.name);
    formData.append('price', form.price);
    if (form.image) {
      formData.append('image', form.image);
    }

    try {
      if (editItemId) {
        await axios.put(`http://localhost:5000/items/${editItemId}`, formData, {
          headers: {
            'Content-Type': 'multipart/form-data',
          },
        });
        setEditItemId(null);
      } else {
        await axios.post('http://localhost:5000/items', formData, {
          headers: {
            'Content-Type': 'multipart/form-data',
          },
        });
      }
      fetchItems();
      setForm({ name: '', price: '', image: null });
    } catch (error) {
      console.error('Error saving item:', error);
    }
  };

  const handleDelete = async (id) => {
    try {
      await axios.delete(`http://localhost:5000/items/${id}`);
      fetchItems();
    } catch (error) {
      console.error('Error deleting item:', error);
    }
  };

  const handleEdit = (item) => {
    setEditItemId(item._id);
    setForm({ name: item.name, price: item.price, image: null });
  };

  return (
    <div className="admin-panel-container">
      {/* Sidebar */}
      <aside className="sidebar">
        <h2>Admin Panel</h2>
        <nav>
          <ul>
            <li><a href="#">Dashboard</a></li>
            <li><a href="#">Manage Courses</a></li>
            <li><a href="#">Settings</a></li>
          </ul>
        </nav>
      </aside>

      {/* Main Content */}
      <main className="main-content">
        <header className="header">
          <h1>Manage Courses</h1>
        </header>

        {/* Form */}
        <section className="form-section">
          <form onSubmit={handleSubmit} className="form">
            <input
              type="text"
              name="name"
              value={form.name}
              onChange={handleChange}
              placeholder="Course Name"
              required
              className="form-input"
            />
            <input
              type="number"
              name="price"
              value={form.price}
              onChange={handleChange}
              placeholder="Price (DT/Month)"
              required
              className="form-input"
            />
            <input
              type="file"
              name="image"
              onChange={handleImageChange}
              className="form-input"
            />
            <button type="submit" className="submit-button">
              {editItemId ? 'Update Course' : 'Add Course'}
            </button>
          </form>
        </section>

        {/* Display Items */}
        <section className="items-section">
          <div className="card-container">
            {items.map((item) => (
              <div className="card" key={item._id}>
                <img
                  src={`http://localhost:5000/${item.image}`}
                  alt={item.name}
                  className="card-image"
                />
                <h2 className="card-title">{item.name}</h2>
                <p className="card-price">{item.price} DT/Month</p>
                <div className="card-actions">
                  <button className="edit-button" onClick={() => handleEdit(item)}>Edit</button>
                  <button className="delete-button" onClick={() => handleDelete(item._id)}>Delete</button>
                </div>
              </div>
            ))}
          </div>
        </section>
      </main>
    </div>
  );
};

export default AdminPanel;
