import React from 'react';
import axios from 'axios';

const ItemList = ({ items, fetchItems, selectItem }) => {
  const handleDelete = async (id) => {
    try {
      await axios.delete(`http://localhost:5000/items/${id}`);
      fetchItems();  // Recharger les éléments après suppression
    } catch (error) {
      console.error('Error deleting item:', error);
    }
  };

  if (!Array.isArray(items)) {
    return <p>Items should be an array. Please check your data.</p>;
  }

  return (
    <ul>
      {items.length > 0 ? (
        items.map((item) => (
          <li key={item._id}>
            <strong>{item.name}</strong> - ${item.price}
            <p>{item.description}</p>
            <button onClick={() => selectItem(item)}>Edit</button>
            <button onClick={() => handleDelete(item._id)}>Delete</button>
          </li>
        ))
      ) : (
        <p>No items available</p> // Message affiché si aucun élément n'est disponible
      )}
    </ul>
  );
};

export default ItemList;
