import React, { useState, useEffect } from 'react';
import axios from 'axios';

const App = () => {
  const [items, setItems] = useState([]); // Tableau des items
  const [form, setForm] = useState({
    name: '',
    description: '',
    price: '',
    image: null, // Pour stocker l'image
  });

  // Récupérer les items de l'API
  const fetchItems = async () => {
    try {
      const response = await axios.get('http://localhost:5000/items');
      setItems(response.data);
    } catch (error) {
      console.error('Error fetching items:', error);
    }
  };

  useEffect(() => {
    fetchItems(); // Récupérer les items au chargement de la page
  }, []);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setForm({ ...form, [name]: value });
  };

  const handleImageChange = (e) => {
    setForm({ ...form, image: e.target.files[0] });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    const formData = new FormData();
    formData.append('name', form.name);
    formData.append('price', form.price);
    formData.append('description', form.description);
    formData.append('image', form.image); // Ajouter l'image

    try {
      await axios.post('http://localhost:5000/items', formData, {
        headers: {
          'Content-Type': 'multipart/form-data', // Spécifier que c'est un formulaire avec fichier
        },
      });
      fetchItems(); // Rafraîchir la liste des items
      setForm({ name: '', description: '', price: '', image: null }); // Réinitialiser le formulaire
    } catch (error) {
      console.error('Error uploading item:', error);
    }
  };

  return (
    <div>
      <h1>Item Manager</h1>
      
      {/* Formulaire d'ajout d'item */}
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          name="name"
          value={form.name}
          onChange={handleChange}
          placeholder="Item Name"
          required
        />
        <textarea
          name="description"
          value={form.description}
          onChange={handleChange}
          placeholder="Item Description"
        />
        <input
          type="number"
          name="price"
          value={form.price}
          onChange={handleChange}
          placeholder="Price"
          required
        />
        <input
          type="file"
          name="image"
          onChange={handleImageChange}
        />
        <button type="submit">Add Item</button>
      </form>

      {/* Affichage des items */}
      <ul>
        {items.map((item) => (
          <li key={item._id}>
            <strong>{item.name}</strong> - ${item.price}
            <p>{item.description}</p>
            {item.image && <img src={`http://localhost:5000/${item.image}`} alt={item.name} width="100" />}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default App;
