import React, { useState, useEffect } from 'react';
import axios from 'axios';
import {
  Box,
  Typography,
  Grid,
  Card,
  CardContent,
  CardMedia,
  Button,
  TextField,
  IconButton,
  Dialog,
  DialogActions,
  DialogContent,
  DialogTitle,
} from '@mui/material';
import { Add, Edit, Delete } from '@mui/icons-material';

const App = () => {
  const [items, setItems] = useState([]);
  const [form, setForm] = useState({
    name: '',
    price: '',
    image: null,
  });
  const [editItemId, setEditItemId] = useState(null); // Track item being edited
  const [dialogOpen, setDialogOpen] = useState(false);

  // Fetch items from API
  const fetchItems = async () => {
    try {
      const response = await axios.get('http://localhost:5000/items');
      setItems(response.data);
    } catch (error) {
      console.error('Error fetching items:', error);
    }
  };

  useEffect(() => {
    fetchItems();
  }, []);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setForm({ ...form, [name]: value });
  };

  const handleImageChange = (e) => {
    setForm({ ...form, image: e.target.files[0] });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    const formData = new FormData();
    formData.append('name', form.name);
    formData.append('price', form.price);
    if (form.image) {
      formData.append('image', form.image);
    }

    try {
      if (editItemId) {
        await axios.put(`http://localhost:5000/items/${editItemId}`, formData, {
          headers: {
            'Content-Type': 'multipart/form-data',
          },
        });
        setEditItemId(null);
      } else {
        await axios.post('http://localhost:5000/items', formData, {
          headers: {
            'Content-Type': 'multipart/form-data',
          },
        });
      }
      fetchItems();
      setForm({ name: '', price: '', image: null });
      setDialogOpen(false);
    } catch (error) {
      console.error('Error saving item:', error);
    }
  };

  const handleDelete = async (id) => {
    try {
      await axios.delete(`http://localhost:5000/items/${id}`);
      fetchItems();
    } catch (error) {
      console.error('Error deleting item:', error);
    }
  };

  const handleEdit = (item) => {
    setEditItemId(item._id);
    setForm({ name: item.name, price: item.price, image: null });
    setDialogOpen(true);
  };

  return (
    <Box sx={{ padding: 4, bgcolor: '#f5f5f5', minHeight: '100vh' }}>
      <Typography variant="h4" gutterBottom>
        Admin Panel - Manage Courses
      </Typography>
      <Button
        variant="contained"
        startIcon={<Add />}
        onClick={() => setDialogOpen(true)}
        sx={{ marginBottom: 3 }}
      >
        Add Course
      </Button>

      {/* Dialog Form */}
      <Dialog open={dialogOpen} onClose={() => setDialogOpen(false)}>
        <DialogTitle>{editItemId ? 'Edit Course' : 'Add Course'}</DialogTitle>
        <DialogContent>
          <TextField
            margin="dense"
            name="name"
            label="Course Name"
            value={form.name}
            onChange={handleChange}
            fullWidth
            required
          />
          <TextField
            margin="dense"
            name="price"
            label="Price (DT/Month)"
            type="number"
            value={form.price}
            onChange={handleChange}
            fullWidth
            required
          />
          <TextField
            margin="dense"
            name="image"
            type="file"
            onChange={handleImageChange}
            fullWidth
          />
        </DialogContent>
        <DialogActions>
          <Button onClick={() => setDialogOpen(false)}>Cancel</Button>
          <Button onClick={handleSubmit} variant="contained">
            {editItemId ? 'Update' : 'Add'}
          </Button>
        </DialogActions>
      </Dialog>

      {/* Items Grid */}
      <Grid container spacing={3}>
        {items.map((item) => (
          <Grid item xs={12} sm={6} md={4} key={item._id}>
            <Card>
              <CardMedia
                component="img"
                height="140"
                image={`http://localhost:5000/${item.image}`}
                alt={item.name}
              />
              <CardContent>
                <Typography variant="h6">{item.name}</Typography>
                <Typography color="text.secondary">{item.price} DT/Month</Typography>
                <Box sx={{ display: 'flex', justifyContent: 'space-between', marginTop: 2 }}>
                  <IconButton color="primary" onClick={() => handleEdit(item)}>
                    <Edit />
                  </IconButton>
                  <IconButton color="error" onClick={() => handleDelete(item._id)}>
                    <Delete />
                  </IconButton>
                </Box>
              </CardContent>
            </Card>
          </Grid>
        ))}
      </Grid>
    </Box>
  );
};

export default App;
